mapper.insert:
INSERT INTO infrCommonCode (
	ifccNameKor
	,ifccNameEng
	,ifccUseNY
	,ifccDelNY
	,ifccDescription
	,infrCodeGroup_ifcgSeq
	,ifccRegDatetime
	,ifccModDatetime
)
VALUES (
	#{ifccNameKor}
	,#{ifccNameEng}
	,#{ifccUseNY}
	,#{ifccDelNY}
	,#{ifccDescription}
	,#{ifcgSeq}
	,now()
	,now()
)


	SELECT last_insert_id()

mapper.update:
UPDATE infrCommonCode
SET
	ifccNameEng = #{ifccNameEng}
	,ifccNameKor = #{ifccNameKor}
	,ifccUseNY = #{ifccUseNY}
	,ifccDelNY = #{ifccDelNY}
	,ifccDescription = #{ifccDescription}
	,infrCodeGroup_ifcgSeq = #{ifcgSeq}
	,ifccModDatetime = now()

WHERE
	ifccSeq = #{ifccSeq}
