mapper.insert:
INSERT INTO infrCodeGroup (
	ifcgNameKor
)
VALUES (
	#{ifcgNameKor}
)


	SELECT last_insert_id()

mapper.update:
UPDATE infrCodeGroup
SET
	ifcgNameEng = #{ifcgNameEng}
	,ifcgNameKor = #{ifcgNameKor}
	,ifcgUseNY = #{ifcgUseNY}
	,ifcgDelNY = #{ifcgDelNY}
	,ifcgModDatetime = now()

WHERE
	ifcgSeq = #{ifcgSeq}
