	,memberMobile
	,memberCarrier
	,memberMailName
	,memberMailDomain
	,memberPlace
	,memberSignDatetime
	,memberModDatetime
	,memberDelNY
)
VALUES (
	#{memberID}
	,#{memberPW}	
	,#{memberName}	
	,#{memberMobile}	
	,2	
	,#{memberMailName}
	,#{memberMailDomain}	
	,#{memberPlace}	
	,now()	
	,now()
	,0	
)

	SELECT last_insert_id()

mapper.select:
SELECT
	COUNT(*)
FROM itemCart
WHERE 1=1
	AND member_seq = #{mainKey}
	AND item_seq = #{itemSeq}
